import React from 'react';

class helloCounter extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            count_hello:0,

        };
    }
    addHello(){
        this.state.count_hello += 1;
    }
    render(){
        return (
            <div>
            <button className = "hello_button" onClick = {()=>{this.addHello()}}>こんにちは!</button>
            <p className = "hello_counter">{this.state.count_hello}</p>
            </div>
            );
    }
}

export default helloCounter;
