import logo from './images/header_image.png';
import './App.scss';
import React from 'react';
import Test from './Test';
import ContactMe from './ContactMe';

export default class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            sayHello:null,
            contactMe:null,
        }
    }
    handleOpenIntroduction(){
        this.setState({sayHello:Test});
    }
    handleOpenContactMe(){
        this.setState({contactMe:ContactMe});
    }
  render(){
      const {sayHello} = this.state.sayHello;
      if(contactMe){return (
          <p>testOK</p>
      );}
      if(sayHello) {return (
      <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          柿崎愛斗のポートフォリオサイト
        </p>
        <div>
        <button className = "hellobutton" onClick = {() => {this.handleOpenIntroduction()}}>こんにちは！</button>
        </div>
      </header>
      <body>
      <Test />
      </body>
      <button className = "contactbutton" onClick = {() => {this.handleOpenContactMe()}}>Contact！</button>
    </div>

  );
      }
    return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          柿崎愛斗のポートフォリオサイト
        </p>
        <div>
        <button className = "hellobutton" onClick = {() => {this.handleOpenIntroduction()}}>こんにちは！</button>
        </div>
      </header>

    </div>
  );
    }
}
