import logo from './images/header_image.png';
import './App.scss';
import React from 'react';
import Test from './Test';
import ContactMe from './ContactMe';
import bow from './images/bow.png';

export default class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            sayHello:null,
            contactMe:null,
        }
    }
    handleOpenIntroduction(){
        this.setState({sayHello:Test});
    }
    handleOpenContactMe(){
        this.setState({contactMe:ContactMe});
    }
  render(){
      const {sayHello} = this.state;
      const {contactMe} = this.state;
      if(contactMe){
          return (
              <div className="App">
                  <img src = {bow} className = "bowimg" />
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          柿崎愛斗のポートフォリオサイト
        </p>
        <div>
        <button className = "hellobutton" onClick = {() => {this.handleOpenIntroduction()}}>こんにちは！</button>
        </div>
      </header>
      <body>
      <Test />
      <button className = "contactbutton" onClick = {() => {this.handleOpenContactMe()}}>Contact！</button>
      </body>
            <footer>
          <ContactMe />
      </footer>
        </div>
          );
      }
      if(sayHello) {return (
      <div className="App">
          <div className = "move_bow" cmanOMat = "move">
      <img src = {bow} className = "bowimg"/>
      </div>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          柿崎愛斗のポートフォリオサイト
        </p>
        <div>
        <button className = "hellobutton" onClick = {() => {this.handleOpenIntroduction()}}>こんにちは！</button>
        </div>
      </header>
      <p className = "arrorw">↓↓↓</p>
      <body>
      <Test />
      <button className = "contactbutton" onClick = {() => {this.handleOpenContactMe()}}>ContactMe！</button>
      </body>

    </div>

  );
      }
    return (
    <div className="App">

        <img src = {bow} className = "bowimg" />
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          柿崎愛斗のポートフォリオサイト
        </p>
        <div>
        <button className = "hellobutton" onClick = {() => {this.handleOpenIntroduction()}}>こんにちは！</button>
        </div>
      </header>

    </div>
  );
    }
}
