import React from 'react';
import ReactDOM from 'react-dom';

export default class Header extends React.Component{
   header = document.getElementById("header"); 
    constructor(props){
        super(props);
        this.state = {
            pos:0,
            hH:this.header.clientHeight,
            lastPos:0,
        }
    }
    onScroll = () =>{
       if(this.state.pos > this.state.hH && this.state.pos > this.state.lastPos) {
        this.header.classList.add('header--unpinned');
        }
  // スクロール位置がヘッダーの高さ分より小さいか
  // またはスクロール位置が最後のスクロール位置より小さい場合はclass名を削除
        if(this.state.pos < this.state.hH || this.state.pos < this.state.lastPos) {
        header.classList.remove('header--unpinned');
        }
        this.setState({lastPos:this.state.pos});
    };


    render(){
        window.addEventListener('scroll', () => {
  // スクロールするごとにpos（現在地）の値を更
        this.setstate({pos:window.scrollY});
        onScroll();
    });
        return (
        <div>
        <header className = "header">
            <h1>Header</h1>
        </header>
        </div>
        );
    }
}
